import { FormGroup,InputLabel,Input,FormControl,Button,makeStyles ,Typography} from "@material-ui/core";
import { useState,useEffect } from "react";
import { getUsers } from '../Service/api';
import { useHistory,useParams } from  'react-router-dom';


const useStyles = makeStyles({
    container: {
        width:'50%',
        margin:'5% 0 0 25%',
        '& > *':{
            marginTop:22
        }
    },
})

const initalValues = {
    name:'',
    username:'',
    email:'',
    phone:''
}

const EditUser = () => {
    const [ user,setUser] = useState(initalValues);
    const {name,username,email,phone} = user;
    const {id} = useParams();
    const classes = useStyles();
    const history = useHistory();

    useEffect(() => {
      loadUserData();  
    },[]);

    const loadUserData = async() =>{
        const response = await getUser(id);
        setUser(response.data);
    }

    const onValueChange = (e) =>{
        setUser({ ...user,[e.target.name]:e.target.value});
    }

    const editUserDetails = async () => {
        await editUser(id,user);
        history.push('./all');
    }

    return(
        <FormGroup className={classes.container}>
            <Typography variant="h5">Edit User</Typography>
            <FormControl>
                <InputLabel>Name</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name="name" value={name} />
            </FormControl>
            <FormControl>
                <InputLabel>UserName</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name="username" value={username}/>
            </FormControl>
            <FormControl>
                <InputLabel>Email</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name="email" value={email}/>
            </FormControl>
            <FormControl>
                <InputLabel>Phone</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name="phone" value={phone}/>
            </FormControl>
            <Button variant="contained" onClick={() => editUserDetails()} color="primary">Add User</Button>
        </FormGroup>
    )
}

export default EditUser;
