import { TableCell,Table,TableBody,TableHead,TableRow,makeStyles,Button } from '@material-ui/core';
import { useEffect , useState} from 'react';
import { getUsers } from "../Service/api";
import { Link } from "react-router-dom";

const useStyles = makeStyles({
    table: {
        width:'90%',
        margin: "50px 0 0 50px"
    },
    thred:{
        '& > * ':{
            background:'#000000',
            color:'#FFFFFF',
            fontSize:20
        }
    },
    row:{
        '& > *':{
            fontSize:18
        }
    }
}) 

const AllUsers = () => {
  
    const [users, setUsers]=useState([]);
    const classes = useStyles();

    useEffect(() =>{
        getAllUsers()
    },[])

    const getAllUsers = async() => {
        const response = await getUsers();
        console.log(response);
        setUsers(response.data);
    }

    const deleteUserData = async(id) => {
        await deleteUser(id);
        getAllUsers();
    }

    return(
        <Table className={classes.table}>
            <TableHead>
                <TableRow className={classes.thred}>
                    <TableCell>ID</TableCell>
                    <TableCell>Name</TableCell>
                    <TableCell>Username</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>Phone</TableCell>
                    <TableCell>Actions</TableCell>
                </TableRow>
            </TableHead>
            <TableBody>
                {
                    users.map(user => (
                        <TableRow className={classes.row}>
                            <TableCell>{user.id}</TableCell>
                            <TableCell>{user.name}</TableCell>
                            <TableCell>{user.username}</TableCell>
                            <TableCell>{user.email}</TableCell>
                            <TableCell>{user.phone  }</TableCell>
                            <TableCell>
                                <Button variant="contained" color="primary" style={{marginRight:15}} component={Link} to={`/edit/${user.id}`}>Edit</Button>
                                <Button variant="contained" color="secondary" onClick={()=>deleteuserData(user.id)}>Delete</Button>
                            </TableCell>
                        </TableRow>
                    ))
                }
            </TableBody>
        </Table>
    )
}

export default AllUsers;
